                        </div>
                      </div>
                    )}

                    {/* Thumbnail Navigation */}
                    {photoViewer.photos.length > 1 && (
                      <div className="flex gap-2 mt-4 justify-center overflow-x-auto pb-2">
                        {photoViewer.photos.map((photo, index) => (
                          <button
                            key={index}
                            onClick={() => setPhotoViewer(prev => ({ 
                              ...prev, 
                              currentIndex: index,
                              zoom: 1, // Reset zoom when changing photos
                              panX: 0,
                              panY: 0
                            }))}
                            className={`flex-shrink-0 w-16 h-16 rounded border-2 overflow-hidden ${
                              index === photoViewer.currentIndex ? 'border-blue-500' : 'border-gray-300'
                            }`}
                          >
                            <img
                              src={`/${photo.url}`}
                              alt={`Thumbnail ${index + 1}`}
                              className="w-full h-full object-cover"
                            />
                          </button>
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Summary Stats - Mobile Optimized */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-2 sm:gap-4">
          <Card>
            <CardContent className="p-3 sm:p-4">
              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
                <div>
                  <p className="text-xs sm:text-sm text-gray-600">Total Dalals</p>
