import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { useToast } from "@/hooks/use-toast";
import { 
  TrendingUp, 
  TrendingDown, 
  DollarSign, 
  Calendar, 
  FileText, 
  BarChart3,
  PieChart,
  Calculator,
  Building2,
  CreditCard,
  BanknoteIcon,
  Receipt,
  Users,
  Download,
  Plus,
  Eye,
  Search,
  Check,
  ChevronDown
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { Navigation } from "@/components/navigation";
import { BackToDashboard } from "@/components/back-to-dashboard";
import type { Farmer } from "@shared/schema";

// Format currency values
const formatCurrency = (amount: number | string) => {
  const num = typeof amount === 'string' ? parseFloat(amount) : amount;
  return new Intl.NumberFormat('en-IN', {
    style: 'currency',
    currency: 'INR',
    minimumFractionDigits: 2,
  }).format(num);
};

// Format percentage
const formatPercentage = (value: number) => {
  return new Intl.NumberFormat('en-IN', {
    style: 'percent',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  }).format(value / 100);
};

export default function FinalAccounts() {
  // Add authentication check
  const { user } = useAuth();

  // Let the page load and handle authentication in the data fetching
  const { toast } = useToast();
  const [selectedFiscalYear, setSelectedFiscalYear] = useState<string>("");
  const [paymentDialog, setPaymentDialog] = useState<{ type: 'received' | 'made', open: boolean }>({ type: 'received', open: false });
  const [expenseDialog, setExpenseDialog] = useState(false);

  // Farmer search state for payment made dialog
  const [farmerSearch, setFarmerSearch] = useState("");
  const [selectedFarmer, setSelectedFarmer] = useState<Farmer | null>(null);
  const [showFarmerDropdown, setShowFarmerDropdown] = useState(false);
  const [suggestedAmount, setSuggestedAmount] = useState<number>(0);
  const [paymentAmount, setPaymentAmount] = useState<string>("");
  const farmerDropdownRef = useRef<HTMLDivElement>(null);

  // Date Range Selection State
  const [dateRangeMode, setDateRangeMode] = useState<'fiscal' | 'custom'>('fiscal');
  const [customStartDate, setCustomStartDate] = useState<string>('');
  const [customEndDate, setCustomEndDate] = useState<string>('');

  // Debug state values on every render
  console.log('üè† Final Accounts State:', { 
    dateRangeMode, 
    customStartDate, 
    customEndDate,
    condition: dateRangeMode === 'custom' && customStartDate && customEndDate
  });

  // Simple test to ensure component loads
  console.log("Final Accounts component is loading...");

  // Get current fiscal year
  const { data: fiscalYearData } = useQuery({
    queryKey: ["/api/accounting/fiscal-year"],
    staleTime: 1000 * 60 * 60, // 1 hour
  });

  // Fetch farmers for search suggestions
  const { data: farmers } = useQuery({
    queryKey: ["/api/farmers", farmerSearch],
    enabled: farmerSearch.length > 0,
    staleTime: 30000, // 30 seconds
  });

  // Fetch farmer's outstanding balance when farmer is selected
  const { data: farmerBalance } = useQuery({
    queryKey: ["/api/accounting/farmer-balance", selectedFarmer?.id],
    enabled: !!selectedFarmer,
    staleTime: 10000, // 10 seconds
  });

  const currentFiscalYear = fiscalYearData?.fiscalYear || "2024-25";

  // Helper function to get preset date ranges
  const getPresetDateRange = (preset: string) => {
    const today = new Date();
    const startDate = new Date();

    switch (preset) {
      case 'last30days':
        startDate.setDate(today.getDate() - 30);
        break;
      case 'last3months':
        startDate.setMonth(today.getMonth() - 3);
        break;
      case 'last6months':
        startDate.setMonth(today.getMonth() - 6);
        break;
      case 'thisMonth':
        startDate.setDate(1);
        break;
      case 'thisQuarter':
        const quarterStart = Math.floor(today.getMonth() / 3) * 3;
        startDate.setMonth(quarterStart, 1);
        break;
      default:
        return null;
    }

    setCustomStartDate(startDate.toISOString().split('T')[0]);
    setCustomEndDate(today.toISOString().split('T')[0]);
    setDateRangeMode('custom');
  };

  // Get final accounts data
  const { data: finalAccounts, isLoading: finalAccountsLoading, error: finalAccountsError } = useQuery({
    queryKey: ["/api/accounting/final-accounts", dateRangeMode, customStartDate, customEndDate, selectedFiscalYear, currentFiscalYear],
    queryFn: async () => {
      const timestamp = Date.now();
      console.log('üîç Frontend Query Function:', { 
        dateRangeMode, 
        customStartDate, 
        customEndDate, 
        timestamp,
        condition: dateRangeMode === 'custom' && customStartDate && customEndDate
      });

      if (dateRangeMode === 'custom' && customStartDate && customEndDate) {
        const url = `/api/accounting/final-accounts?startDate=${customStartDate}&endDate=${customEndDate}&_t=${timestamp}`;
        console.log('üìÖ Custom date range URL:', url);
        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache',
            'Expires': '0'
          }
        });
        if (!response.ok) throw new Error('Failed to fetch');
        const result = await response.json();
        console.log('üìä API Response (Date Range):', { netProfit: result.netProfit, totalIncome: result.totalIncome });
        return result;
      } else {
        const url = `/api/accounting/final-accounts?_t=${timestamp}`;
        console.log('üóìÔ∏è Fiscal year URL:', url);
        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Cache-Control': 'no-cache, no-store, must-revalidate',
            'Pragma': 'no-cache',
            'Expires': '0'
          }
        });
        if (!response.ok) throw new Error('Failed to fetch');
        const result = await response.json();
        console.log('üìä API Response (Fiscal):', { netProfit: result.netProfit, totalIncome: result.totalIncome });
        return result;
      }
    },
    enabled: true, // Always enabled
    retry: 1,
    refetchOnMount: true,
    refetchOnWindowFocus: false,
    staleTime: 5000, // Consider data stale after 5 seconds
    cacheTime: 60000, // Cache for 1 minute
  });

  // Get profitability analysis
  const { data: farmerProfitability } = useQuery({
    queryKey: ["/api/accounting/profitability/farmers", dateRangeMode, customStartDate, customEndDate, selectedFiscalYear || currentFiscalYear],
    queryFn: async () => {
      const params = new URLSearchParams();

      if (dateRangeMode === 'custom' && customStartDate && customEndDate) {
        params.set('startDate', customStartDate);
        params.set('endDate', customEndDate);
        console.log('üîç Fetching farmer profitability with date range:', { startDate: customStartDate, endDate: customEndDate });
      } else {
        params.set('fiscalYear', selectedFiscalYear || currentFiscalYear);
        console.log('üîç Fetching farmer profitability with fiscal year:', selectedFiscalYear || currentFiscalYear);
      }

      const response = await fetch(`/api/accounting/profitability/farmers?${params}`, {
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });
      if (!response.ok) throw new Error('Failed to fetch farmer profitability');
      return response.json();
    },
    staleTime: 0,
    cacheTime: 0,
  });

  const { data: buyerProfitability } = useQuery({
    queryKey: ["/api/accounting/profitability/buyers", dateRangeMode, customStartDate, customEndDate, selectedFiscalYear || currentFiscalYear],
    queryFn: async () => {
      const params = new URLSearchParams();

      if (dateRangeMode === 'custom' && customStartDate && customEndDate) {
        params.set('startDate', customStartDate);
        params.set('endDate', customEndDate);
        console.log('üîç Fetching buyer profitability with date range:', { startDate: customStartDate, endDate: customEndDate });
      } else {
        params.set('fiscalYear', selectedFiscalYear || currentFiscalYear);
        console.log('üîç Fetching buyer profitability with fiscal year:', selectedFiscalYear || currentFiscalYear);
      }

      const response = await fetch(`/api/accounting/profitability/buyers?${params}`, {
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });
      if (!response.ok) throw new Error('Failed to fetch buyer profitability');
      return response.json();
    },
    staleTime: 0,
    cacheTime: 0,
  });

  // Get GST liability
  const { data: gstLiability } = useQuery({
    queryKey: ["/api/accounting/gst-liability", dateRangeMode, customStartDate, customEndDate, selectedFiscalYear || currentFiscalYear],
    queryFn: async () => {
      const params = new URLSearchParams();

      if (dateRangeMode === 'custom' && customStartDate && customEndDate) {
        params.set('startDate', customStartDate);
        params.set('endDate', customEndDate);
        console.log('üîç Fetching GST liability with date range:', { startDate: customStartDate, endDate: customEndDate });
