import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Navigation } from "@/components/navigation";
import { LotForm } from "@/components/lot-form";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Plus,
  Search,
  Package,
  CheckCircle,
  Printer,
  Calendar,
  Filter,
} from "lucide-react";
import { Link } from "wouter";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { BackToDashboard } from "@/components/back-to-dashboard";
import { generateAPMCPDF, formatDateForAPMC } from "@/lib/pdf-generator";

interface Lot {
  id: number;
  lotNumber: string;
  farmerId: number;
  numberOfBags: number;
  vehicleRent: string;
  advance: string;
  varietyGrade: string;
  unloadHamali: string;
  lotPrice?: string;
  status: string;
  farmer: {
    name: string;
    mobile: string;
    place: string;
  };
  buyer?: {
    name: string;
  };
}

const getStatusColor = (status: string) => {
  switch (status) {
    case "active":
      return "bg-yellow-100 text-yellow-800";
    case "completed":
      return "bg-green-100 text-green-800";
    default:
      return "bg-gray-100 text-gray-800";
  }
};

export default function Lots() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // Today's date
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isPrintDialogOpen, setIsPrintDialogOpen] = useState(false);
  const [printStartDate, setPrintStartDate] = useState("");
  const [printEndDate, setPrintEndDate] = useState("");
  const [printType, setPrintType] = useState<"all" | "active" | "completed">("all");
  const { toast } = useToast();

  const { data: allLots, isLoading } = useQuery<Lot[]>({
    queryKey: ["/api/lots", searchTerm, selectedDate],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.append("search", searchTerm);
      if (selectedDate) params.append("date", selectedDate);

      const url = `/api/lots${params.toString() ? `?${params.toString()}` : ""}`;
      const response = await fetch(url, { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch lots");
      return response.json();
    },
  });

  // Show both active and completed lots from selected date
  const lots = allLots || [];

  const { data: tenant } = useQuery({
    queryKey: ["/api/tenant"],
    queryFn: async () => {
      const response = await fetch("/api/tenant", { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch tenant info");
      return response.json();
    },
  });

  const completeLotMutation = useMutation({
    mutationFn: async (lotId: number) => {
      return await apiRequest("PUT", `/api/lots/${lotId}`, {
        status: "completed",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/lots"] });
      toast({
        title: "Success",
        description: "Lot marked as completed and ready for billing",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleCompleteLot = (lotId: number) => {
    completeLotMutation.mutate(lotId);
  };

  const handlePrintAllLots = async () => {
    if (!allLots || !tenant) {
      toast({
        title: "Error",
        description: "Unable to print - missing data",
        variant: "destructive",
      });
      return;
    }

    // Filter lots based on selected criteria
    let filteredLots = allLots || [];

    // Filter by status
    if (printType === "active") {
      filteredLots = filteredLots.filter(lot => lot.status === "active");
    } else if (printType === "completed") {
      filteredLots = filteredLots.filter(lot => lot.status === "completed");
    }

    // Filter by date range
    if (printStartDate || printEndDate) {
      filteredLots = filteredLots.filter(lot => {
        const lotDate = new Date(lot.createdAt || Date.now()).toISOString().split('T')[0];
        const startMatch = !printStartDate || lotDate >= printStartDate;
        const endMatch = !printEndDate || lotDate <= printEndDate;
        return startMatch && endMatch;
      });
    }

    const sortedLots = [...filteredLots].sort((a, b) => {
      const lotA = parseInt(a.lotNumber.replace(/\D/g, ""), 10) || 0;
      const lotB = parseInt(b.lotNumber.replace(/\D/g, ""), 10) || 0;
      return lotA - lotB;
    });

    const apmcData = {
      place: tenant.place || tenant.name,
      traderName: tenant.name,
      traderMobile: tenant.mobileNumber || "N/A",
      traderCode: tenant.apmcCode,
      traderAddress: `${tenant.address || tenant.name} - Trader Code: ${tenant.apmcCode}`,
      date: formatDateForAPMC(new Date()),
      lots: sortedLots.map((lot) => ({
        lotNumber: lot.lotNumber,
        farmerName: lot.farmer.name,
        place: lot.farmer.place,
        numberOfBags: lot.numberOfBags,
      })),
    };

    try {
      await generateAPMCPDF(apmcData);
      toast({
        title: "Success",
        description: `${printType} lots printed successfully`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: (error as Error).message,
        variant: "destructive",
      });
    }

    // Close the print dialog
    setIsPrintDialogOpen(false);
  };



  return (
    <div className="min-h-screen bg-gray-50">
      <Navigation />

      <div className="max-w-7xl mx-auto px-3 sm:px-4 lg:px-8 py-4 sm:py-8">
        <BackToDashboard />
        <div className="mb-6 sm:mb-8">
          <h1 className="text-xl sm:text-2xl font-bold text-gray-900 mb-4 sm:mb-6">
            Daily Lots Management
          </h1>

          <div className="mb-4 p-3 sm:p-4 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-2">
              <div className="flex-1">
                <h3 className="text-base sm:text-lg font-medium text-blue-900">Daily Lots Overview</h3>
                <p className="text-sm text-blue-700">
                  Showing lots for {new Date(selectedDate).toLocaleDateString('en-IN')} - 
                  Active: {lots?.filter(lot => lot.status === 'active').length || 0}, 
                  Completed: {lots?.filter(lot => lot.status === 'completed').length || 0}
                </p>
              </div>
              <div className="text-xl sm:text-2xl font-bold text-blue-900 text-center sm:text-right">
                {lots?.length || 0} Total Lots
              </div>
            </div>
          </div>

          <div className="flex flex-col gap-3 sm:gap-4">
            <div className="flex flex-col sm:flex-row gap-3">
              <div className="relative flex-1 sm:flex-none">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <Input
                  type="text"
                  placeholder="Search lots by lot number, farmer name..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 w-full sm:w-72 min-h-[44px]"
                />
              </div>

              <div className="flex items-center gap-2">
                <Calendar className="h-4 w-4 text-gray-400 flex-shrink-0" />
                <Input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="w-full sm:w-40 min-h-[44px]"
                />
              </div>
            </div>

            <div className="flex flex-col sm:flex-row gap-2">
              <Dialog open={isPrintDialogOpen} onOpenChange={setIsPrintDialogOpen}>
                <DialogTrigger asChild>
                  <Button 
                    variant="outline"
                    className="bg-green-50 border-green-300 text-green-700 hover:bg-green-100 w-full sm:w-auto min-h-[44px]"
                  >
                    <Printer className="h-4 w-4 mr-2" />
                    Print Lots Report
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-md">
                  <DialogHeader>
                    <DialogTitle>Print Lots Report</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="printType">Report Type</Label>
                      <div className="mt-2 space-y-2">
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="printType"
                            value="all"
                            checked={printType === "all"}
                            onChange={(e) => setPrintType(e.target.value as any)}
                            className="w-4 h-4"
                          />
                          <span>All Lots (Active + Completed)</span>
                        </label>
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="printType"
                            value="active"
                            checked={printType === "active"}
                            onChange={(e) => setPrintType(e.target.value as any)}
                            className="w-4 h-4"
                          />
                          <span>Active Lots Only</span>
                        </label>
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="printType"
                            value="completed"
                            checked={printType === "completed"}
                            onChange={(e) => setPrintType(e.target.value as any)}
                            className="w-4 h-4"
                          />
                          <span>Completed Lots Only</span>
                        </label>
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="dateRange">Date Range (Optional)</Label>
                      <div className="mt-2 space-y-2">
                        <div>
                          <Label htmlFor="startDate" className="text-sm">Start Date</Label>
                          <Input
                            id="startDate"
                            type="date"
                            value={printStartDate}
                            onChange={(e) => setPrintStartDate(e.target.value)}
                            className="mt-1"
                          />
                        </div>
                        <div>
                          <Label htmlFor="endDate" className="text-sm">End Date</Label>
                          <Input
                            id="endDate"
                            type="date"
                            value={printEndDate}
                            onChange={(e) => setPrintEndDate(e.target.value)}
                            className="mt-1"
                          />
                        </div>
                      </div>
                    </div>

                    <div className="flex gap-2 pt-4">
                      <Button
                        onClick={handlePrintAllLots}
                        className="flex-1 bg-green-600 hover:bg-green-700"
                      >
                        <Printer className="h-4 w-4 mr-2" />
                        Generate Report
                      </Button>
                      <Button
                        variant="outline"
                        onClick={() => setIsPrintDialogOpen(false)}
                        className="flex-1"
                      >
                        Cancel
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>

              <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogTrigger asChild>
                  <Button className="bg-primary hover:bg-primary/90">
                    <Plus className="h-4 w-4 mr-2" />
                    Create New Lot
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Create New Lot</DialogTitle>
                  </DialogHeader>
                  <LotForm onSuccess={() => setIsDialogOpen(false)} />
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </div>

        {/* Lots Table */}
        <Card className="bg-white shadow-sm border border-gray-200">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Lot No.
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Farmer Details
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Bags & Variety
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Financial Details
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {isLoading ? (
                  <tr>
                    <td colSpan={6} className="px-6 py-4 text-center text-gray-500">
                      Loading lots...
                    </td>
                  </tr>
                ) : lots && lots.length > 0 ? (
                  lots.map((lot) => (
                    <tr key={lot.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">
                          {lot.lotNumber}
                        </div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm">
                          <div className="font-medium text-gray-900">
                            {lot.farmer.name}
                          </div>
                          <div className="text-gray-500">{lot.farmer.mobile}</div>
                          <div className="text-gray-500">{lot.farmer.place}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm">
                          <div className="font-medium text-gray-900">
                            {lot.numberOfBags} bags
                          </div>
                          <div className="text-gray-500">{lot.varietyGrade}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm">
                          <div className="text-gray-900">
                            Vehicle: ‚Çπ{lot.vehicleRent}
                          </div>
                          <div className="text-gray-500">
                            Advance: ‚Çπ{lot.advance}
                          </div>
                          <div className="text-gray-500">
                            Hamali: ‚Çπ{lot.unloadHamali}
                          </div>
                          {lot.lotPrice && (
                            <div className="font-medium text-gray-900">
                              Price: ‚Çπ{lot.lotPrice}
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <Badge className={getStatusColor(lot.status)}>
                          {lot.status}
                        </Badge>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <div className="flex space-x-2">
                          <Link href={`/lots/${lot.id}/bags`}>
                            <Button
                              variant="ghost"
                              size="sm"
                              className="text-primary hover:text-primary/80"
                            >
                              <Package className="h-4 w-4 mr-1" />
                              Bag Entry
                            </Button>
                          </Link>
                          {lot.status === "active" && lot.lotPrice && (
                            <Button
                              variant="ghost"
                              size="sm"
                              className="text-green-600 hover:text-green-700"
                              onClick={() => handleCompleteLot(lot.id)}
                              disabled={completeLotMutation.isPending}
                            >
                              <CheckCircle className="h-4 w-4 mr-1" />
                              Complete
                            </Button>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan={6} className="px-6 py-12 text-center">
                      <div className="flex flex-col items-center space-y-4">
                        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center">
                          <Package className="h-8 w-8 text-gray-400" />
                        </div>
                        <div>
                          <h3 className="text-lg font-medium text-gray-900">
                            No active lots found
                          </h3>
                          <p className="text-gray-500 mt-1">
                            {searchTerm
                              ? "No active lots match your search criteria"
                              : "All lots have been completed or no lots exist. Create a new lot to get started."}
                          </p>
                        </div>
                        {!searchTerm && (
                          <Button
                            onClick={() => setIsDialogOpen(true)}
                            className="bg-primary hover:bg-primary/90"
                          >
                            <Plus className="h-4 w-4 mr-2" />
                            Create First Lot
                          </Button>
                        )}
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>


        </Card>
      </div>
    </div>
  );
}

client/src/pages/missing-bags.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Navigation } from "@/components/navigation";
import { AlertTriangle, CheckCircle, Package, RefreshCw, Calendar, User, Hash, ExternalLink } from "lucide-react";
import { Link } from "wouter";

interface MissingBagDetail {
  lotId: number;
  lotNumber: string;
  farmerId: number;
  farmerName: string;
  totalBags: number;
  enteredBags: number;
  missingBagNumbers: number[];
  emptyWeightBags: number[];
  missingCount: number;
  emptyWeightCount: number;
  completionPercentage: number;
  status: string;
  createdAt: string;
}

interface MissingBagsSummary {
  totalLotsForDate: number;
  lotsWithMissingBags: number;
  lotsComplete: number;
  totalMissingBags: number;
  totalEmptyWeightBags: number;
  date: string;
}

interface MissingBagsResponse {
  summary: MissingBagsSummary;
  missingBagsDetails: MissingBagDetail[];
  dateLots: any[];
}

export default function MissingBagsPage() {
  const [selectedDate, setSelectedDate] = useState<string>(new Date().toISOString().split('T')[0]);
  const [refreshKey, setRefreshKey] = useState(0);

  const { data: missingBagsData, isLoading, refetch } = useQuery<MissingBagsResponse>({
    queryKey: ['/api/missing-bags', selectedDate, refreshKey],
    queryFn: async () => {
      const response = await fetch(`/api/missing-bags?date=${selectedDate}`);
      if (!response.ok) throw new Error('Failed to fetch missing bags data');
      return response.json();
    }
  });

  const handleRefresh = () => {
    setRefreshKey(prev => prev + 1);
    refetch();
  };

  const handleDateChange = (date: string) => {
    setSelectedDate(date);
  };

  const summary = missingBagsData?.summary;
  const missingBags = missingBagsData?.missingBagsDetails || [];

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Navigation />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Missing Bags Detection</h1>
          <p className="mt-2 text-gray-600 dark:text-gray-400">
            Monitor and track incomplete bag entries across lots
          </p>
        </div>

        {/* Date Selection and Controls */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="h-5 w-5" />
              Analysis Date Selection
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-end">
              <div className="flex-1 max-w-sm">
                <Label htmlFor="analysis-date" className="text-sm font-medium">
                  Select Date
                </Label>
                <Input
                  id="analysis-date"
                  type="date"
                  value={selectedDate}
                  onChange={(e) => handleDateChange(e.target.value)}
                  className="mt-1"
                />
              </div>
              <Button onClick={handleRefresh} variant="outline" className="gap-2">
                <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
                Refresh Analysis
              </Button>
            </div>
          </CardContent>
        </Card>

        {isLoading ? (
          <div className="flex items-center justify-center h-64">
            <div className="text-center">
              <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-4 text-blue-600" />
              <p className="text-gray-600 dark:text-gray-400">Analyzing lots for missing bags...</p>
            </div>
          </div>
        ) : (
          <>
            {/* Summary Cards */}
            {summary && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-8">
                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Total Lots</p>
                        <p className="text-2xl font-bold text-gray-900 dark:text-white">
                          {summary.totalLotsForDate}
                        </p>
                      </div>
                      <Package className="h-8 w-8 text-blue-600" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Complete Lots</p>
                        <p className="text-2xl font-bold text-green-600">
                          {summary.lotsComplete}
                        </p>
                      </div>
                      <CheckCircle className="h-8 w-8 text-green-600" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Incomplete Lots</p>
                        <p className="text-2xl font-bold text-red-600">
                          {summary.lotsWithMissingBags}
                        </p>
                      </div>
                      <AlertTriangle className="h-8 w-8 text-red-600" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Missing Bags</p>
                        <p className="text-2xl font-bold text-orange-600">
                          {summary.totalMissingBags}
                        </p>
                      </div>
                      <Hash className="h-8 w-8 text-orange-600" />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Empty Weights</p>
                        <p className="text-2xl font-bold text-yellow-600">
                          {summary.totalEmptyWeightBags}
                        </p>
                      </div>
                      <Package className="h-8 w-8 text-yellow-600" />
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}

            {/* Status Alert */}
            {summary && (
              <Alert className={summary.lotsWithMissingBags > 0 ? "border-red-200 bg-red-50 mb-6" : "border-green-200 bg-green-50 mb-6"}>
                {summary.lotsWithMissingBags > 0 ? (
                  <AlertTriangle className="h-4 w-4 text-red-600" />
                ) : (
                  <CheckCircle className="h-4 w-4 text-green-600" />
                )}
                <AlertDescription>
                  {summary.lotsWithMissingBags > 0 ? (
                    <div className="text-red-800">
                      <strong>Action Required:</strong> {summary.lotsWithMissingBags} lots have incomplete bag entries on {summary.date}. 
                      Please complete missing bag entries to ensure accurate tracking.
                    </div>
                  ) : (
                    <div className="text-green-800">
                      <strong>All Complete:</strong> All {summary.totalLotsForDate} lots have complete bag entries on {summary.date}. 
                      No missing bags detected.
                    </div>
                  )}
                </AlertDescription>
              </Alert>
            )}

            {/* Missing Bags Details */}
            {missingBags.length > 0 ? (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <AlertTriangle className="h-5 w-5 text-red-600" />
                    Lots Requiring Attention ({missingBags.length})
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {missingBags.map((lot) => (
                      <div key={lot.lotId} className="border border-red-200 rounded-lg p-4 bg-red-50 dark:bg-red-900/20">
                        {/* Lot Header */}
                        <div className="flex items-center justify-between mb-3">
                          <div className="flex items-center gap-3">
                            <Badge variant="outline" className="font-mono text-red-800 border-red-300">
                              {lot.lotNumber}
                            </Badge>
                            <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
                              <User className="h-4 w-4" />
                              {lot.farmerName}
                            </div>
                            <Badge variant={lot.status === 'active' ? 'default' : 'secondary'}>
                              {lot.status}
                            </Badge>
                          </div>
                          <div className="text-right">
                            <div className="text-sm font-medium">
                              {lot.enteredBags}/{lot.totalBags} bags entered
                            </div>
                            <div className="text-xs text-gray-500">
                              {lot.completionPercentage}% complete
                            </div>
                          </div>
                        </div>

                        {/* Missing Bags Info */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          {lot.missingBagNumbers.length > 0 && (
                            <div className="bg-white dark:bg-gray-800 p-3 rounded border-l-4 border-red-500">
                              <h4 className="font-medium text-red-800 dark:text-red-400 mb-2">
                                Missing Bags ({lot.missingCount})
                              </h4>
                              <div className="flex flex-wrap gap-1">
                                {lot.missingBagNumbers.map((bagNum) => (
                                  <Badge key={bagNum} variant="destructive" className="text-xs">
                                    #{bagNum}
                                  </Badge>
                                ))}
                              </div>
                            </div>
                          )}

                          {lot.emptyWeightBags.length > 0 && (
                            <div className="bg-white dark:bg-gray-800 p-3 rounded border-l-4 border-yellow-500">
                              <h4 className="font-medium text-yellow-800 dark:text-yellow-400 mb-2">
                                Empty Weight Bags ({lot.emptyWeightCount})
                              </h4>
                              <div className="flex flex-wrap gap-1">
                                {lot.emptyWeightBags.map((bagNum) => (
                                  <Badge key={bagNum} variant="outline" className="text-xs border-yellow-500 text-yellow-700">
                                    #{bagNum}
                                  </Badge>
                                ))}
                              </div>
                            </div>
                          )}
                        </div>

                        {/* Action Button */}
                        <div className="mt-4 pt-3 border-t border-red-200">
                          <Link href="/lots">
                            <Button size="sm" variant="outline" className="gap-2">
                              <ExternalLink className="h-4 w-4" />
                              Go to Lots Page
                            </Button>
                          </Link>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            ) : summary && summary.totalLotsForDate > 0 ? (
              <Card>
                <CardContent className="p-8 text-center">
                  <CheckCircle className="h-16 w-16 text-green-600 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                    All Lots Complete!
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400">
                    All {summary.totalLotsForDate} lots on {summary.date} have complete bag entries.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="p-8 text-center">
                  <Package className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                    No Lots Found
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400">
                    No lots were created on {selectedDate}.
                  </p>
                </CardContent>
              </Card>
            )}
          </>
        )}
      </div>
    </div>
  );
}

