  });

  // Initialize form hook at the top level - never conditionally
  const gstForm = useForm<GSTSettings>({
    resolver: zodResolver(gstSettingsSchema),
    defaultValues: {
      sgst: 2.5,
      cgst: 2.5,
      cess: 0.6,
      unloadHamali: 3,
      packaging: 5,
      weighingFee: 2,
      apmcCommission: 2,
      rokPercentage: 3,
    },
  });

  const { data: settings, isLoading, error } = useQuery<TenantSettings>({
    queryKey: ["/api/settings"],
    retry: 1,
    queryFn: async () => {
      const response = await fetch("/api/settings", { credentials: "include" });
      if (!response.ok) {
        throw new Error(`Failed to fetch settings: ${response.status}`);
      }
      return response.json();
    },
  });

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: Partial<TenantSettings>) => {
      await apiRequest("PUT", "/api/settings", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Success",
        description: "Settings updated successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update form when settings data loads - MUST be before any early returns
  useEffect(() => {
    if (settings) {
      gstForm.reset({
        sgst: settings.gstSettings.sgst,
        cgst: settings.gstSettings.cgst,
        cess: settings.gstSettings.cess,
        unloadHamali: settings.gstSettings.unloadHamali,
        packaging: settings.gstSettings.packaging || 5,
        packagingWeight: settings.gstSettings.packagingWeight || 0,
        weighingFee: settings.gstSettings.weighingFee || 2,
        apmcCommission: settings.gstSettings.apmcCommission || 2,
        rokPercentage: settings.gstSettings.rokPercentage || 3,
      });
    }
  }, [settings, gstForm]);

  const onGSTSubmit = (data: GSTSettings) => {
    updateSettingsMutation.mutate({
      gstSettings: data,
    });
  };

  if (isLoading) {
    return (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
        <p className="mt-4 text-gray-600">Loading settings...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Card className="w-full max-w-md mx-auto">
        <CardHeader>
          <CardTitle className="text-red-600">Authentication Required</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-gray-600">Please log in to access settings.</p>
          <Button 
            onClick={() => window.location.href = '/auth'} 
            className="mt-4 w-full"
          >
            Go to Login
          </Button>
        </CardContent>
      </Card>
    );
  }



  // Only show settings if user is admin or super admin
  if (user?.role !== 'admin' && user?.role !== 'super_admin') {
    return (
      <Card className="text-center py-12">
        <CardContent>
          <h3 className="text-lg font-medium text-gray-900">Access Denied</h3>
          <p className="text-gray-500 mt-1">
            You don't have permission to access settings.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Tabs value={activeTab} onValueChange={setActiveTab}>
      <TabsList className="mb-8">
        <TabsTrigger value="gst" className="flex items-center space-x-2">
          <Receipt className="h-4 w-4" />
          <span>GST & Charges</span>
        </TabsTrigger>
        <TabsTrigger value="general" className="flex items-center space-x-2">
          <SettingsIcon className="h-4 w-4" />
          <span>General</span>
        </TabsTrigger>
      </TabsList>

          <TabsContent value="gst">
            <Card>
              <CardHeader>
                <CardTitle>GST & Charges Configuration</CardTitle>
                <p className="text-sm text-gray-600">
                  Configure tax rates and charges that will be automatically calculated
                </p>
              </CardHeader>
              <CardContent>
                <form onSubmit={gstForm.handleSubmit(onGSTSubmit)} className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                      <Label htmlFor="sgst">SGST Rate (%)</Label>
                      <Input
                        id="sgst"
                        type="number"
                        step="0.01"
                        min="0"
                        max="100"
                        {...gstForm.register("sgst", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="cgst">CGST Rate (%)</Label>
                      <Input
                        id="cgst"
                        type="number"
                        step="0.01"
                        min="0"
                        max="100"
                        {...gstForm.register("cgst", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="cess">CESS Rate (%)</Label>
                      <Input
                        id="cess"
                        type="number"
                        step="0.01"
                        min="0"
                        max="100"
                        {...gstForm.register("cess", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="unloadHamali">Unload Hamali (‚Çπ per bag)</Label>
                      <Input
                        id="unloadHamali"
                        type="number"
                        step="0.01"
                        min="0"
                        {...gstForm.register("unloadHamali", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="packaging">Packaging (‚Çπ per bag)</Label>
                      <Input
                        id="packaging"
                        type="number"
                        step="0.01"
                        min="0"
                        {...gstForm.register("packaging", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="weighingFee">Weighing Fee (‚Çπ per bag)</Label>
                      <Input
                        id="weighingFee"
                        type="number"
                        step="0.01"
                        min="0"
                        {...gstForm.register("weighingFee", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="apmcCommission">APMC Commission (%)</Label>
                      <Input
                        id="apmcCommission"
                        type="number"
                        step="0.01"
                        min="0"
                        max="100"
                        {...gstForm.register("apmcCommission", { valueAsNumber: true })}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="rokPercentage">Rok Rate (%)</Label>
                      <Input
                        id="rokPercentage"
                        type="number"
                        step="0.01"
                        min="0"
                        max="100"
                        {...gstForm.register("rokPercentage", { valueAsNumber: true })}
                      />
                    </div>
                  </div>

                  <Button 
                    type="submit" 
                    disabled={updateSettingsMutation.isPending}
                    className="w-full"
                  >
                    {updateSettingsMutation.isPending ? "Saving..." : "Save Settings"}
                  </Button>
                </form>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="general">
            <Card>
              <CardHeader>
                <CardTitle>General Settings</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-600">General settings will be available here.</p>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
  );
}

client/src/pages/staff-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Switch } from "@/components/ui/switch";
import { Users, Plus, Edit, Trash2, UserPlus, Shield, ShieldCheck, X, ArrowLeft } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User, InsertUser } from "@shared/schema";
import { useLocation } from "wouter";
import { UnifiedInput } from "@/components/ui/unified-input";

const staffFormSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Invalid email format"),
  role: z.enum(["admin", "staff"]),
  isActive: z.boolean().default(true),
});

type StaffFormData = z.infer<typeof staffFormSchema>;

export default function StaffManagement() {
  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const { data: staff = [], isLoading } = useQuery<User[]>({
    queryKey: ["/api/staff"],
  });

  const form = useForm<StaffFormData>({
    resolver: zodResolver(staffFormSchema),
    defaultValues: {
      username: "",
      password: "",
      name: "",
      email: "",
      role: "staff",
      isActive: true,
    },
  });

  const createStaffMutation = useMutation({
    mutationFn: async (data: StaffFormData) => {
      return await apiRequest("POST", "/api/staff", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Staff member created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
      setDialogOpen(false);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateStaffMutation = useMutation({
    mutationFn: async (data: { id: number; updates: Partial<StaffFormData> }) => {
      return await apiRequest("PATCH", `/api/staff/${data.id}`, data.updates);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Staff member updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
      setDialogOpen(false);
      setEditingUser(null);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteStaffMutation = useMutation({
    mutationFn: async (id: number) => {
      return await apiRequest("DELETE", `/api/staff/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Staff member deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const toggleActiveMutation = useMutation({
    mutationFn: async (data: { id: number; isActive: boolean }) => {
      return await apiRequest(`/api/staff/${data.id}`, "PATCH", { isActive: data.isActive });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/staff"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleVoiceInput = (field: keyof StaffFormData, value: string) => {
    form.setValue(field, value);
  };

  const onSubmit = (data: StaffFormData) => {
    if (editingUser) {
      updateStaffMutation.mutate({ id: editingUser.id, updates: data });
    } else {
      createStaffMutation.mutate(data);
    }
  };

  const handleEdit = (user: User) => {
    setEditingUser(user);
    form.reset({
      username: user.username,
      password: "", // Don't show existing password
      name: user.name,
      email: user.email,
      role: user.role as "admin" | "staff",
      isActive: user.isActive,
    });
    setDialogOpen(true);
  };

  const handleDelete = (id: number) => {
    deleteStaffMutation.mutate(id);
  };

  const toggleActive = (id: number, isActive: boolean) => {
    toggleActiveMutation.mutate({ id, isActive });
  };

  const getRoleIcon = (role: string) => {
    switch (role) {
      case "admin":
        return <ShieldCheck className="h-4 w-4 text-blue-600" />;
      case "staff":
        return <Shield className="h-4 w-4 text-gray-500" />;
      default:
        return <Users className="h-4 w-4 text-gray-400" />;
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="text-gray-500">Loading staff...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setLocation('/')}
            className="flex items-center gap-2 hover:bg-gray-100"
          >
            <ArrowLeft className="h-4 w-4" />
            Back
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Staff Management</h1>
            <p className="text-gray-600 mt-2">Manage your APMC center staff members</p>
          </div>
        </div>

        <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => {
              setEditingUser(null);
              form.reset();
            }}>
              <UserPlus className="h-4 w-4 mr-2" />
              Add Staff Member
            </Button>
          </DialogTrigger>

          <DialogContent className="max-w-md">
            <DialogHeader className="flex flex-row items-center justify-between">
              <DialogTitle>
                {editingUser ? "Edit Staff Member" : "Add New Staff Member"}
              </DialogTitle>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setDialogOpen(false)}
                className="h-6 w-6 p-0 hover:bg-gray-100"
              >
                <X className="h-4 w-4" />
              </Button>
            </DialogHeader>

            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username</FormLabel>
                      <FormControl>
                        <UnifiedInput
                          {...field}
                          placeholder="Enter username"
                          type="text"
                          voiceType="text"
                          required
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>
                        {editingUser ? "New Password (leave empty to keep current)" : "Password"}
                      </FormLabel>
                      <FormControl>
                        <UnifiedInput
                          {...field}
                          type="password"
                          placeholder="Enter password"
                          voiceType="text"
                          required
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Full Name</FormLabel>
                      <FormControl>
                        <UnifiedInput
                          {...field}
                          placeholder="Enter full name"
                          type="text"
                          voiceType="text"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <UnifiedInput
                          {...field}
                          type="email"
                          placeholder="Enter email"
                          voiceType="email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Role</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select role" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="admin">Admin</SelectItem>
                          <SelectItem value="staff">Staff</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="isActive"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <FormLabel>Active Account</FormLabel>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex gap-2 pt-4">
                  <Button
                    type="submit"
                    disabled={createStaffMutation.isPending || updateStaffMutation.isPending}
                    className="flex-1"
                  >
                    {editingUser ? "Update Staff" : "Create Staff"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setDialogOpen(false)}
                    className="flex-1"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5" />
            Staff Members ({staff.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {staff.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Users className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p>No staff members found</p>
              <p className="text-sm">Add your first staff member to get started</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Username</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Role</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {staff.map((user: User) => (
                  <TableRow key={user.id}>
                    <TableCell className="font-medium">{user.name}</TableCell>
                    <TableCell>{user.username}</TableCell>
                    <TableCell>{user.email}</TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        {getRoleIcon(user.role)}
                        <span className="capitalize">{user.role}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant={user.isActive ? "default" : "secondary"}>
                        {user.isActive ? "Active" : "Inactive"}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleEdit(user)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>

                        <Switch
                          checked={user.isActive}
                          onCheckedChange={(checked) => toggleActive(user.id, checked)}
                          disabled={toggleActiveMutation.isPending}
                        />

                        <AlertDialog>
                          <AlertDialogTrigger asChild>
                            <Button variant="ghost" size="sm" className="text-red-600 hover:text-red-700">
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </AlertDialogTrigger>
                          <AlertDialogContent>
                            <AlertDialogHeader>
                              <AlertDialogTitle>Delete Staff Member</AlertDialogTitle>
                              <AlertDialogDescription>
                                Are you sure you want to delete {user.name}? This action cannot be undone.
                              </AlertDialogDescription>
                            </AlertDialogHeader>
                            <AlertDialogFooter>
                              <AlertDialogCancel>Cancel</AlertDialogCancel>
                              <AlertDialogAction
                                onClick={() => handleDelete(user.id)}
                                className="bg-red-600 hover:bg-red-700"
                              >
                                Delete
                              </AlertDialogAction>
                            </AlertDialogFooter>
                          </AlertDialogContent>
                        </AlertDialog>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/stock-reports.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Package, TrendingUp, TrendingDown, Download, AlertTriangle } from "lucide-react";
import { format } from "date-fns";

interface StockInventory {
  id: number;
  itemName: string;
  itemDescription?: string;
  currentQuantity: string;
  availableQuantity: string;
  reservedQuantity: string;
  unit: string;
  avgPurchaseRate?: string;
  lastPurchaseRate?: string;
  lastPurchaseDate?: string;
  minimumStockLevel: string;
  buyerId: number;
  buyerName?: string;
}

interface StockMovement {
  id: number;
  movementType: string;
  referenceType: string;
  referenceId?: number;
  quantityChange: string;
  balanceAfter: string;
  ratePerUnit?: string;
  totalValue?: string;
  itemName?: string;
  unit?: string;
  createdAt: string;
  buyerId: number;
}

interface Buyer {
  id: number;
  companyName: string;
  contactPerson: string;
  mobile: string;
}

export default function StockReports() {
  const [filters, setFilters] = useState({
    startDate: "",
    endDate: "",
    buyerId: "all",
    itemName: ""
  });

  const [appliedFilters, setAppliedFilters] = useState(filters);
  const [editingStockId, setEditingStockId] = useState<number | null>(null);
  const [editingMinStock, setEditingMinStock] = useState("");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get buyers for filter dropdown
  const { data: buyers = [] } = useQuery<Buyer[]>({
    queryKey: ["/api/buyers"],
  });

  // Get stock inventory
  const { data: inventory = [], isLoading: inventoryLoading } = useQuery<StockInventory[]>({
    queryKey: ["/api/stock-inventory", appliedFilters.buyerId],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (appliedFilters.buyerId && appliedFilters.buyerId !== "all") params.append('buyerId', appliedFilters.buyerId);

      const response = await fetch(`/api/stock-inventory?${params}`);
      if (!response.ok) throw new Error('Failed to fetch inventory');
      return response.json();
    }
  });

  // Get stock movements
  const { data: movements = [], isLoading: movementsLoading } = useQuery<StockMovement[]>({
    queryKey: ["/api/stock-movements", appliedFilters.buyerId, appliedFilters.startDate, appliedFilters.endDate, appliedFilters.itemName],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (appliedFilters.buyerId && appliedFilters.buyerId !== "all") params.append('buyerId', appliedFilters.buyerId);
      if (appliedFilters.startDate) params.append('startDate', appliedFilters.startDate);
      if (appliedFilters.endDate) params.append('endDate', appliedFilters.endDate);
      if (appliedFilters.itemName) params.append('itemName', appliedFilters.itemName);

      const response = await fetch(`/api/stock-movements?${params}`);
      if (!response.ok) throw new Error('Failed to fetch movements');
      return response.json();
    }
  });

  const applyFilters = () => {
    setAppliedFilters({ ...filters });
  };

  const clearFilters = () => {
    const emptyFilters = { startDate: "", endDate: "", buyerId: "all", itemName: "" };
    setFilters(emptyFilters);
    setAppliedFilters(emptyFilters);
  };

  // Mutation to update minimum stock level
  const updateMinStockMutation = useMutation({
    mutationFn: async ({ stockId, minStock }: { stockId: number, minStock: string }) => {
      return await apiRequest("PUT", `/api/stock-inventory/${stockId}/min-stock`, { minimumStockLevel: minStock });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stock-inventory"] });
      toast({
        title: "Stock Alert Updated",
        description: "Minimum stock level has been updated successfully.",
      });
      setEditingStockId(null);
      setEditingMinStock("");
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update minimum stock level.",
        variant: "destructive",
      });
    },
  });

  const startEditingMinStock = (stockId: number, currentMinStock: string) => {
    setEditingStockId(stockId);
    setEditingMinStock(currentMinStock);
  };

  const handleUpdateMinStock = async (stockId: number) => {
    if (editingMinStock && parseFloat(editingMinStock) >= 0) {
      updateMinStockMutation.mutate({ stockId, minStock: editingMinStock });
    } else {
      setEditingStockId(null);
      setEditingMinStock("");
    }
  };

  const downloadInventoryCSV = () => {
    const headers = ['Item Name', 'Description', 'Current Qty', 'Available Qty', 'Unit', 'Avg Rate', 'Last Rate', 'Min Stock'];
    const csvContent = [
      headers.join(','),
      ...inventory.map(item => [
        `"${item.itemName}"`,
        `"${item.itemDescription || ''}"`,
        item.currentQuantity,
        item.availableQuantity,
        item.unit,
        item.avgPurchaseRate || '0',
        item.lastPurchaseRate || '0',
        item.minimumStockLevel
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `stock-inventory-${format(new Date(), 'yyyy-MM-dd')}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadMovementsCSV = () => {
    const headers = ['Date', 'Item', 'Movement Type', 'Quantity Change', 'Balance After', 'Rate', 'Total Value'];
    const csvContent = [
      headers.join(','),
      ...movements.map(mov => [
        format(new Date(mov.createdAt), 'dd/MM/yyyy HH:mm'),
        `"${mov.itemName || 'Unknown'}"`,
        mov.movementType,
        mov.quantityChange,
        mov.balanceAfter,
        mov.ratePerUnit || '0',
        mov.totalValue || '0'
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `stock-movements-${format(new Date(), 'yyyy-MM-dd')}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  // Calculate summary statistics
  const totalItems = inventory.length;
  const lowStockItems = inventory.filter(item => 
    parseFloat(item.availableQuantity) <= parseFloat(item.minimumStockLevel)
  ).length;
  const totalValue = inventory.reduce((sum, item) => 
    sum + (parseFloat(item.currentQuantity) * parseFloat(item.avgPurchaseRate || '0')), 0
  );

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Stock Reports</h1>
          <p className="text-muted-foreground">Monitor inventory levels and track stock movements</p>
        </div>
      </div>

      {/* Filters Section */}
      <Card>
        <CardHeader>
          <CardTitle>Filters</CardTitle>
          <CardDescription>Filter stock data by date range, buyer, and item name</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div className="space-y-2">
              <Label htmlFor="startDate">Start Date</Label>
              <Input
                id="startDate"
                type="date"
                value={filters.startDate}
                onChange={(e) => setFilters({ ...filters, startDate: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="endDate">End Date</Label>
              <Input
                id="endDate"
                type="date"
                value={filters.endDate}
                onChange={(e) => setFilters({ ...filters, endDate: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="buyer">Buyer</Label>
              <Select value={filters.buyerId} onValueChange={(value) => setFilters({ ...filters, buyerId: value })}>
                <SelectTrigger>
                  <SelectValue placeholder="All Buyers" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Buyers</SelectItem>
                  {buyers.map((buyer) => (
                    <SelectItem key={buyer.id} value={buyer.id.toString()}>
                      {buyer.companyName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="itemName">Item Name</Label>
              <Input
                id="itemName"
                placeholder="Search items..."
                value={filters.itemName}
                onChange={(e) => setFilters({ ...filters, itemName: e.target.value })}
              />
            </div>
            <div className="flex items-end gap-2">
              <Button onClick={applyFilters} className="flex-1">
                Apply
              </Button>
              <Button variant="outline" onClick={clearFilters}>
                Clear
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Package className="h-4 w-4" />
              Total Items
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{totalItems}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <AlertTriangle className="h-4 w-4 text-orange-500" />
              Low Stock Items
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-500">{lowStockItems}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Stock Value</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">‚Çπ{totalValue.toLocaleString('en-IN')}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Movements</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{movements.length}</div>
          </CardContent>
        </Card>
      </div>

      {/* Tabs for Inventory and Movements */}
      <Tabs defaultValue="inventory" className="space-y-4">
        <TabsList>
          <TabsTrigger value="inventory">Current Inventory</TabsTrigger>
          <TabsTrigger value="movements">Stock Movements</TabsTrigger>
        </TabsList>

        <TabsContent value="inventory">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Current Stock Inventory</CardTitle>
                  <CardDescription>Real-time inventory levels across all items</CardDescription>
                </div>
                <Button onClick={downloadInventoryCSV} disabled={inventory.length === 0}>
                  <Download className="h-4 w-4 mr-2" />
                  Export CSV
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              {inventoryLoading ? (
                <div className="text-center py-4">Loading inventory...</div>
              ) : inventory.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No inventory items found
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Item Name</TableHead>
                        <TableHead>Description</TableHead>
                        <TableHead className="text-right">Current Qty</TableHead>
                        <TableHead className="text-right">Available Qty</TableHead>
                        <TableHead>Unit</TableHead>
                        <TableHead className="text-right">Avg Rate</TableHead>
                        <TableHead className="text-right">Stock Value</TableHead>
                        <TableHead className="text-right">Min Stock</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {inventory.map((item) => {
                        const isLowStock = parseFloat(item.availableQuantity) <= parseFloat(item.minimumStockLevel);
                        const stockValue = parseFloat(item.currentQuantity) * parseFloat(item.avgPurchaseRate || '0');

                        return (
                          <TableRow key={item.id}>
                            <TableCell className="font-medium">{item.itemName}</TableCell>
                            <TableCell>{item.itemDescription || '-'}</TableCell>
                            <TableCell className="text-right">{parseFloat(item.currentQuantity).toLocaleString()}</TableCell>
                            <TableCell className="text-right">{parseFloat(item.availableQuantity).toLocaleString()}</TableCell>
                            <TableCell>{item.unit}</TableCell>
                            <TableCell className="text-right">‚Çπ{parseFloat(item.avgPurchaseRate || '0').toLocaleString('en-IN')}</TableCell>
                            <TableCell className="text-right">‚Çπ{stockValue.toLocaleString('en-IN')}</TableCell>
                            <TableCell className="text-right">
                              {editingStockId === item.id ? (
                                <Input
                                  type="number"
                                  value={editingMinStock}
                                  onChange={(e) => setEditingMinStock(e.target.value)}
                                  className="w-20 h-8"
                                  onBlur={() => handleUpdateMinStock(item.id)}
                                  onKeyPress={(e) => e.key === 'Enter' && handleUpdateMinStock(item.id)}
                                  autoFocus
                                />
                              ) : (
                                <span 
                                  className="cursor-pointer hover:bg-gray-100 px-2 py-1 rounded"
                                  onClick={() => startEditingMinStock(item.id, item.minimumStockLevel)}
                                >
                                  {parseFloat(item.minimumStockLevel).toLocaleString()}
                                </span>
                              )}
                            </TableCell>
                            <TableCell>
                              {isLowStock ? (
                                <Badge variant="destructive">Low Stock</Badge>
                              ) : (
                                <Badge variant="secondary">Good</Badge>
                              )}
                            </TableCell>
                            <TableCell>
                              <Button 
                                variant="outline" 
                                size="sm"
                                onClick={() => startEditingMinStock(item.id, item.minimumStockLevel)}
                              >
                                Edit Alert
                              </Button>
                            </TableCell>
                          </TableRow>
                        );
                      })}
                    </TableBody>
                  </Table>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="movements">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Stock Movements</CardTitle>
                  <CardDescription>Track all inventory transactions and changes</CardDescription>
                </div>
                <Button onClick={downloadMovementsCSV} disabled={movements.length === 0}>
                  <Download className="h-4 w-4 mr-2" />
                  Export CSV
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              {movementsLoading ? (
                <div className="text-center py-4">Loading movements...</div>
              ) : movements.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No stock movements found for the selected criteria
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Date & Time</TableHead>
                        <TableHead>Item Name</TableHead>
                        <TableHead>Movement Type</TableHead>
                        <TableHead className="text-right">Quantity Change</TableHead>
                        <TableHead className="text-right">Balance After</TableHead>
                        <TableHead className="text-right">Rate</TableHead>
                        <TableHead className="text-right">Total Value</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {movements.map((movement) => {
                        const isInbound = parseFloat(movement.quantityChange) > 0;

                        return (
                          <TableRow key={movement.id}>
                            <TableCell>{format(new Date(movement.createdAt), 'dd/MM/yyyy HH:mm')}</TableCell>
                            <TableCell className="font-medium">{movement.itemName || 'Unknown'}</TableCell>
                            <TableCell>
                              <Badge variant={isInbound ? "secondary" : "outline"}>
                                {isInbound ? <TrendingUp className="h-3 w-3 mr-1" /> : <TrendingDown className="h-3 w-3 mr-1" />}
                                {movement.movementType}
                              </Badge>
                            </TableCell>
                            <TableCell className={`text-right ${isInbound ? 'text-green-600' : 'text-red-600'}`}>
                              {isInbound ? '+' : ''}{parseFloat(movement.quantityChange).toLocaleString()}
                            </TableCell>
                            <TableCell className="text-right">{parseFloat(movement.balanceAfter).toLocaleString()}</TableCell>
                            <TableCell className="text-right">‚Çπ{parseFloat(movement.ratePerUnit || '0').toLocaleString('en-IN')}</TableCell>
                            <TableCell className="text-right">‚Çπ{parseFloat(movement.totalValue || '0').toLocaleString('en-IN')}</TableCell>
                          </TableRow>
                        );
                      })}
                    </TableBody>
                  </Table>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

