import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Download, FileText, Printer, Eye, CheckCircle, AlertCircle, Calendar, History, Plus } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { BackToDashboard } from "@/components/back-to-dashboard";

// Enhanced Tax Invoice Interface matching backend
interface TaxInvoice {
  invoiceNumber: string;
  invoiceDate: string;
  hsnCode: string;
  seller: {
    companyName: string;
    apmcCode: string;
    address: string;
    mobile: string;
    gstin: string;
    pan: string;
    fssai: string;
  };
  buyer: {
    companyName: string;
    contactPerson: string;
    address: string;
    mobile: string;
    gstin: string;
    pan: string;
  };
  items: Array<{
    lotNo: string;
    itemName: string;
    hsnCode: string;
    bags: number;
    weightKg: number;
    weightQuintals: number;
    ratePerQuintal: number;
    basicAmount: number;
  }>;
  calculations: {
    basicAmount: number;
    packaging: number;
    hamali: number;
    weighingCharges: number;
    commission: number;
    cess: number;
    taxableAmount: number;
    sgst: number;
    cgst: number;
    igst: number;
    totalGst: number;
    totalAmount: number;
  };
  bankDetails: {
    bankName: string;
    accountNumber: string;
    ifscCode: string;
    accountHolder: string;
    branchName: string;
    branchAddress: string;
  };
}

interface Buyer {
  id: number;
  name: string;
  contactPerson: string;
  mobile: string;
  address: string;
  gstNumber: string;
  panNumber: string;
  hsnCode: string;
}

interface InvoiceRecord {
  id: number;
  invoiceNumber: string;
  invoiceDate: string;
  basicAmount: string;
  totalAmount: string;
  totalBags: number;
  totalWeight: string;
  status: string;
  createdAt: string;
}

export default function TaxInvoiceEnhanced() {
  const [selectedBuyerId, setSelectedBuyerId] = useState<number | null>(null);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [activeTab, setActiveTab] = useState("generate");
  const [selectedInvoice, setSelectedInvoice] = useState<TaxInvoice | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Set default date range to current month
  useEffect(() => {
    const today = new Date();
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
    const endOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);

    setStartDate(startOfMonth.toISOString().slice(0, 10));
    setEndDate(endOfMonth.toISOString().slice(0, 10));
  }, []);

  // Fetch all buyers
  const { data: buyers = [], isLoading: buyersLoading } = useQuery<Buyer[]>({
    queryKey: ["/api/buyers"],
  });

  // Check if tax invoice already exists for selected buyer
  const { data: invoiceCheck, isLoading: checkLoading } = useQuery({
    queryKey: ["/api/tax-invoice", selectedBuyerId, "check"],
    queryFn: async () => {
      if (!selectedBuyerId) return null;
      const response = await fetch(`/api/tax-invoice/${selectedBuyerId}/check`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }
      return response.json();
    },
    enabled: !!selectedBuyerId,
  });

  // Fetch latest tax invoice for selected buyer
  const { data: latestInvoice, isLoading: invoiceLoading } = useQuery<TaxInvoice>({
    queryKey: ["/api/tax-invoice", selectedBuyerId],
    queryFn: async () => {
      if (!selectedBuyerId) return null;
      const response = await fetch(`/api/tax-invoice/${selectedBuyerId}`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }
      return response.json();
    },
    enabled: !!selectedBuyerId && invoiceCheck?.exists,
  });

  // Fetch all historical invoices with date range filtering
  const { data: allInvoices = [], isLoading: allInvoicesLoading } = useQuery<InvoiceRecord[]>({
    queryKey: ["/api/tax-invoices", selectedBuyerId, startDate, endDate],
    queryFn: async () => {
      if (!selectedBuyerId) return [];
      let url = `/api/tax-invoices/${selectedBuyerId}`;
      if (startDate && endDate) {
        url += `?startDate=${startDate}&endDate=${endDate}`;
      }
      const response = await fetch(url, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }
      return response.json();
    },
    enabled: !!selectedBuyerId && activeTab === "history",
  });

  // Generate new tax invoice
  const generateInvoiceMutation = useMutation({
    mutationFn: async (buyerId: number) => {
      return await apiRequest("POST", `/api/tax-invoice/${buyerId}`, {});
    },
    onSuccess: (data) => {
      toast({
        title: "Success",
        description: "Tax invoice generated and saved successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/tax-invoice", selectedBuyerId, "check"] });
      queryClient.invalidateQueries({ queryKey: ["/api/tax-invoice", selectedBuyerId] });
      queryClient.invalidateQueries({ queryKey: ["/api/tax-invoices", selectedBuyerId] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to generate tax invoice",
        variant: "destructive",
      });
    },
  });

  const formatCurrency = (amount: number | string | undefined | null) => {
    if (amount === undefined || amount === null) return '‚Çπ0.00';
    const num = typeof amount === 'string' ? parseFloat(amount) : amount;
    if (isNaN(num)) return '‚Çπ0.00';
    return `‚Çπ${num.toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN');
  };

  const generatePrintableInvoice = (invoice: TaxInvoice) => {
    const printWindow = window.open('', '_blank');
    if (!printWindow) return;

    const invoiceHtml = `
      <!DOCTYPE html>
      <html>
        <head>
          <meta charset="utf-8">
          <title>Tax Invoice - ${invoice.invoiceNumber}</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; font-size: 12px; line-height: 1.4; }
            .header { text-align: center; margin-bottom: 20px; border-bottom: 2px solid #000; padding-bottom: 10px; }
            .invoice-title { font-size: 18px; font-weight: bold; margin-bottom: 5px; }
            .company-info { display: flex; justify-content: space-between; margin-bottom: 20px; }
            .seller, .buyer { width: 48%; }
            .section-title { font-weight: bold; margin-bottom: 5px; text-decoration: underline; }
            table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
            th, td { border: 1px solid #000; padding: 8px; text-align: left; }
            th { background-color: #f0f0f0; font-weight: bold; }
            .calculations { margin-top: 20px; }
            .total-row { font-weight: bold; background-color: #f0f0f0; }
            .bank-details { margin-top: 20px; border: 1px solid #000; padding: 10px; }
            @media print { body { margin: 0; } }
          </style>
        </head>
        <body>
          <div class="header">
            <div class="invoice-title">TAX INVOICE</div>
            <div>Invoice No: ${invoice.invoiceNumber} | Date: ${invoice.invoiceDate}</div>
          </div>

          <div class="company-info">
            <div class="seller">
              <div class="section-title">SELLER DETAILS</div>
              <div><strong>${invoice.seller.companyName}</strong></div>
              <div>APMC Code: ${invoice.seller.apmcCode}</div>
              <div>${invoice.seller.address}</div>
              <div>Mobile: ${invoice.seller.mobile}</div>
              <div>GSTIN: ${invoice.seller.gstin}</div>
              <div>PAN: ${invoice.seller.pan}</div>
              <div>FSSAI: ${invoice.seller.fssai}</div>
            </div>
            <div class="buyer">
              <div class="section-title">BUYER DETAILS</div>
              <div><strong>${invoice.buyer.companyName}</strong></div>
              <div>Contact: ${invoice.buyer.contactPerson}</div>
              <div>${invoice.buyer.address}</div>
              <div>Mobile: ${invoice.buyer.mobile}</div>
              <div>GSTIN: ${invoice.buyer.gstin}</div>
              <div>PAN: ${invoice.buyer.pan}</div>
            </div>
          </div>

          <table>
            <thead>
              <tr>
                <th>Lot No</th>
                <th>Item</th>
                <th>HSN Code</th>
                <th>Bags</th>
                <th>Weight (Kg)</th>
                <th>Weight (Qtl)</th>
                <th>Rate/Qtl</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              ${invoice.items.map(item => `
                <tr>
                  <td>${item.lotNo}</td>
                  <td>${item.itemName}</td>
                  <td>${item.hsnCode}</td>
                  <td>${item.bags}</td>
                  <td>${item.weightKg}</td>
                  <td>${item.weightQuintals.toFixed(2)}</td>
                  <td>${formatCurrency(item.ratePerQuintal)}</td>
                  <td>${formatCurrency(item.basicAmount)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>

          <div class="calculations">
            <table style="width: 50%; margin-left: auto;">
              <tr><td>Basic Amount:</td><td>${formatCurrency(invoice.calculations.basicAmount)}</td></tr>
              <tr><td>Packaging:</td><td>${formatCurrency(invoice.calculations.packaging)}</td></tr>
              <tr><td>Hamali:</td><td>${formatCurrency(invoice.calculations.hamali)}</td></tr>
              <tr><td>Weighing Charges:</td><td>${formatCurrency(invoice.calculations.weighingCharges)}</td></tr>
              <tr><td>Commission:</td><td>${formatCurrency(invoice.calculations.commission)}</td></tr>
              <tr><td>CESS @ 0.6%:</td><td>${formatCurrency(invoice.calculations.cess)}</td></tr>
              <tr><td>SGST @ 2.5%:</td><td>${formatCurrency(invoice.calculations.sgst)}</td></tr>
              <tr><td>CGST @ 2.5%:</td><td>${formatCurrency(invoice.calculations.cgst)}</td></tr>
              <tr class="total-row"><td><strong>Total Amount:</strong></td><td><strong>${formatCurrency(invoice.calculations.totalAmount)}</strong></td></tr>
            </table>
          </div>

          <div class="bank-details">
            <div class="section-title">BANK DETAILS</div>
            <div>Bank: ${invoice.bankDetails.bankName}</div>
            <div>Account No: ${invoice.bankDetails.accountNumber}</div>
            <div>IFSC: ${invoice.bankDetails.ifscCode}</div>
            <div>Account Holder: ${invoice.bankDetails.accountHolder}</div>
          </div>
        </body>
      </html>
    `;

    printWindow.document.write(invoiceHtml);
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  const downloadInvoice = (invoice: TaxInvoice) => {
    const invoiceHtml = `<!-- Same HTML content as print function -->`;
    const blob = new Blob([invoiceHtml], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `tax-invoice-${invoice.invoiceNumber}.html`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const selectedBuyer = buyers.find(b => b.id === selectedBuyerId);

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <BackToDashboard />

      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-sm border p-6">
          <div className="flex items-center gap-2 mb-6">
            <FileText className="h-6 w-6 text-blue-600" />
            <h1 className="text-2xl font-bold text-gray-900">Tax Invoice Management</h1>
          </div>

          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="generate" className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                Generate Invoice
              </TabsTrigger>
              <TabsTrigger value="history" className="flex items-center gap-2">
                <History className="h-4 w-4" />
                Invoice History
              </TabsTrigger>
            </TabsList>

            <TabsContent value="generate" className="space-y-6 mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>Generate New Tax Invoice</CardTitle>
                  <CardDescription>
                    Select a buyer to generate tax invoice for today's completed lots
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label htmlFor="buyer">Select Buyer</Label>
                    <Select
                      value={selectedBuyerId?.toString() || ""}
                      onValueChange={(value) => setSelectedBuyerId(parseInt(value))}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Choose a buyer..." />
                      </SelectTrigger>
                      <SelectContent>
                        {buyers.map((buyer) => (
                          <SelectItem key={buyer.id} value={buyer.id.toString()}>
                            {buyer.name} - {buyer.contactPerson}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {selectedBuyerId && (
                    <div className="space-y-4">
                      {checkLoading ? (
                        <Alert>
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>Checking invoice status...</AlertDescription>
                        </Alert>
                      ) : invoiceCheck?.exists ? (
                        <div className="space-y-4">
                          <Alert>
                            <CheckCircle className="h-4 w-4" />
                            <AlertDescription>
                              Tax invoice already exists for {selectedBuyer?.name}.
                            </AlertDescription>
                          </Alert>

                          {latestInvoice && (
                            <Card>
                              <CardHeader>
                                <CardTitle className="text-lg">Latest Invoice</CardTitle>
                              </CardHeader>
                              <CardContent className="space-y-4">
                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                  <div>
                                    <span className="font-medium">Invoice Number:</span>
                                    <div>{latestInvoice.invoiceNumber}</div>
                                  </div>
                                  <div>
                                    <span className="font-medium">Date:</span>
                                    <div>{latestInvoice.invoiceDate}</div>
                                  </div>
                                  <div>
                                    <span className="font-medium">Total Amount:</span>
                                    <div className="font-bold text-green-600">
                                      {formatCurrency(latestInvoice.calculations.totalAmount)}
                                    </div>
                                  </div>
                                  <div>
                                    <span className="font-medium">Items:</span>
                                    <div>{latestInvoice.items.length} lots</div>
                                  </div>
                                </div>

                                <div className="flex gap-2">
                                  <Button
                                    onClick={() => generatePrintableInvoice(latestInvoice)}
                                    variant="outline"
                                    size="sm"
                                  >
                                    <Printer className="h-4 w-4 mr-2" />
                                    Print
                                  </Button>
                                  <Button
                                    onClick={() => downloadInvoice(latestInvoice)}
                                    variant="outline"
                                    size="sm"
                                  >
                                    <Download className="h-4 w-4 mr-2" />
                                    Download
                                  </Button>
                                </div>
                              </CardContent>
                            </Card>
                          )}
                        </div>
                      ) : (
                        <div className="space-y-4">
                          <Alert>
                            <AlertCircle className="h-4 w-4" />
                            <AlertDescription>
                              No tax invoice found for {selectedBuyer?.name}. You can generate a new one.
                            </AlertDescription>
                          </Alert>

                          <Button
                            onClick={() => generateInvoiceMutation.mutate(selectedBuyerId)}
                            disabled={generateInvoiceMutation.isPending}
                            className="w-full sm:w-auto"
                          >
                            {generateInvoiceMutation.isPending ? "Generating..." : "Generate Tax Invoice"}
                          </Button>
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="history" className="space-y-6 mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>Invoice History</CardTitle>
                  <CardDescription>
                    View all historical tax invoices with date range filtering
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <Label htmlFor="buyer-history">Select Buyer</Label>
                      <Select
                        value={selectedBuyerId?.toString() || ""}
                        onValueChange={(value) => setSelectedBuyerId(parseInt(value))}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Choose a buyer..." />
                        </SelectTrigger>
                        <SelectContent>
                          {buyers.map((buyer) => (
                            <SelectItem key={buyer.id} value={buyer.id.toString()}>
                              {buyer.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label htmlFor="start-date">Start Date</Label>
                      <Input
                        id="start-date"
                        type="date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                      />
                    </div>
                    <div>
                      <Label htmlFor="end-date">End Date</Label>
                      <Input
                        id="end-date"
                        type="date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                      />
                    </div>
                  </div>

                  {selectedBuyerId && (
                    <div className="space-y-4">
                      {allInvoicesLoading ? (
                        <Alert>
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>Loading invoice history...</AlertDescription>
                        </Alert>
                      ) : allInvoices.length > 0 ? (
                        <div className="border rounded-lg overflow-hidden">
                          <Table>
                            <TableHeader>
                              <TableRow>
                                <TableHead>Invoice Number</TableHead>
                                <TableHead>Date</TableHead>
                                <TableHead>Basic Amount</TableHead>
                                <TableHead>Total Amount</TableHead>
                                <TableHead>Bags</TableHead>
                                <TableHead>Weight (Kg)</TableHead>
                                <TableHead>Status</TableHead>
                                <TableHead>Actions</TableHead>
                              </TableRow>
                            </TableHeader>
                            <TableBody>
                              {allInvoices.map((invoice) => (
                                <TableRow key={invoice.id}>
                                  <TableCell className="font-medium">
                                    {invoice.invoiceNumber}
                                  </TableCell>
                                  <TableCell>{formatDate(invoice.invoiceDate)}</TableCell>
                                  <TableCell>{formatCurrency(invoice.basicAmount)}</TableCell>
                                  <TableCell className="font-semibold">
                                    {formatCurrency(invoice.totalAmount)}
                                  </TableCell>
                                  <TableCell>{invoice.totalBags}</TableCell>
                                  <TableCell>{parseFloat(invoice.totalWeight).toFixed(2)}</TableCell>
                                  <TableCell>
                                    <Badge variant={invoice.status === 'generated' ? 'default' : 'secondary'}>
                                      {invoice.status}
                                    </Badge>
                                  </TableCell>
                                  <TableCell>
                                    <Dialog>
                                      <DialogTrigger asChild>
                                        <Button variant="outline" size="sm">
                                          <Eye className="h-4 w-4 mr-1" />
                                          View
                                        </Button>
                                      </DialogTrigger>
                                      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
                                        <DialogHeader>
                                          <DialogTitle>Invoice Details - {invoice.invoiceNumber}</DialogTitle>
                                        </DialogHeader>
                                        <div className="text-sm text-gray-600">
                                          Invoice preview functionality would be implemented here
                                        </div>
                                      </DialogContent>
                                    </Dialog>
                                  </TableCell>
                                </TableRow>
                              ))}
                            </TableBody>
                          </Table>
                        </div>
                      ) : (
                        <Alert>
                          <AlertCircle className="h-4 w-4" />
                          <AlertDescription>
                            No invoices found for {selectedBuyer?.name} in the selected date range.
                          </AlertDescription>
                        </Alert>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

